mapper.select:
select * from icredit_api_base_hi where del_flag = 0
and api_base_id = #{apiId}
limit 1

select

from icredit_api_base_hi
where del_flag = 0 and api_base_id = #{apiId}

    and publish_status = #{publishStatus}


    and publish_time >= #{publishDateStart}


    and publish_time <= #{publishDateEnd}

order by api_version desc
limit #{pageStartNum},#{pageSize}

select count(1)
from icredit_api_base_hi
where del_flag = 0 and api_base_id = #{apiId}

    and publish_status = #{publishStatus}


    and publish_time >= #{publishDateStart}


    and publish_time <= #{publishDateEnd}

