mapper.select:
(
  SELECT appGroup.id,appGroup.`name`,appGroup.generate_id AS generateId,NULL AS certificationType,NULL AS isEnable,NULL AS period,
        appGroup.create_by AS createBy,appGroup.create_time AS createTime,appGroup.`desc`,NULL AS appGroupId
  FROM icredit_app_group appGroup LEFT JOIN icredit_app app ON appGroup.id = app.app_group_id
  WHERE appGroup.del_flag = 0

      AND appGroup.name LIKE concat ('%', #{appGroupName}, '%')


      AND app.name LIKE concat ('%', #{appName}, '%')


      AND app.certification_type = #{certificationType}


      AND app.is_enable = #{isEnable}


      AND app.period = #{period}

  ORDER BY appGroup.create_time DESC
)
UNION ALL
(
  SELECT app.id,app.`name`,app.generate_id AS generateId,app.certification_type AS certificationType,app.is_enable AS isEnable,app.period,
        app.create_by AS createBy,app.create_time AS createTime,app.`desc`,app_group_id AS appGroupId
  FROM icredit_app_group appGroup LEFT JOIN icredit_app app ON appGroup.id = app.app_group_id
  WHERE app.del_flag = 0

        AND appGroup.name LIKE concat ('%', #{appGroupName}, '%')


        AND app.name LIKE concat ('%', #{appName}, '%')


        AND app.certification_type = #{certificationType}


        AND app.is_enable = #{isEnable}


        AND app.period = #{period}

  ORDER BY app.create_time DESC
)

select 1
from icredit_app_group


        AND name = #{name}


        AND id != #{id}

    AND del_flag = 0

limit 1

select 1
from icredit_app_group


        AND generate_id = #{generateId}


        AND id != #{id}

    AND del_flag = 0

limit 1
