mapper.select:
select auth.id id, auth.app_id appId, auth.api_id apiId, auth.auth_config_id authConfigId, (SELECT app.generate_id from icredit_app app where app.del_flag=0 and app.id=auth.app_id) appGenerateId from icredit_auth auth inner join icredit_auth_config config ON auth.auth_config_id=config.id where auth.del_flag = 0 AND config.del_flag=0
and auth.api_id = #{apiId}
#{item}

SELECT api.name as apiName, api.path apiPath, api.id apiId, iac.period_begin authPeriodBegin,
iac.period_end authPeroidEnd, iac.allow_call authAllowCall, ia.app_id appId, ia.id authId, api.interface_source apiInterfaceSource
FROM icredit_auth ia
inner join icredit_auth_config iac ON ia.auth_config_id=iac.id
inner JOIN icredit_api_base api ON ia.api_id=api.id
 1=1 AND ia.del_flag=0 and iac.del_flag=0 and api.del_flag=0 AND ia.app_id=#{appId}
     AND INSTR(api.name , #{apiName})>0 
     AND INSTR(api.path , #{apiPath})>0 

        AND (iac.period_begin >= #{periodBegin}
         OR iac.period_begin = -1 
        )
        AND (iac.period_end <= #{periodEnd}
         OR iac.period_end = -1 
        )


         AND iac.period_begin = -1 AND iac.period_end = -1 
         AND iac.period_begin > 0 AND iac.period_end > 0 


         AND iac.allow_call = -1 
         AND iac.allow_call >= 0 


 LIMIT #{pageStart}, #{pageSize}

